function Header(value) {
    this._value = value;
};

Header.prototype._value = undefined;

Header.prototype.getKey = function() {
    throw new Error("Headers must implement #getKey()");
};

Header.prototype.getValue = function() {
    return this._value;
};

Header.prototype.setValue = function(value) {
    this._value = value;
};

module.exports = Header;
